    public void setPregunta(Pregunta p) {
        AreaPregunta.setText(p.getEnunciado());
        rdBtnOpcion1.setText(p.getRespuestas(1));
        rdBtnOpcion2.setText(p.getRespuestas(2));
        rdBtnOpcion3.setText(p.getRespuestas(3));
        rdBtnOpcion4.setText(p.getRespuestas(4));
    }
    
